def swaps(arr):
    n = len(arr)
    s = 0
    # print(len(arr))

    for i in range(1, n):
        k = i
        while k > 0 and arr[k] < arr[k - 1]:
            arr[k], arr[k - 1] = arr[k - 1], arr[k]
            s += 1
            k -= 1
    # print(arr)
    # s_arr = sorted(arr)
    # uns = 0
    # for i in range(len(arr)):
    #     if arr[i] != s_arr[i]:
    #         uns += 1
            # print(f'{i},{arr[i]},{s_arr[i]}')
    # print(arr == sorted(arr))
    # print(uns)
    return s

